import { useState, useEffect } from 'react';
import { BASE_URL } from '../globals';
import axios from 'axios';
import CategoryCard from '../components/CategoryCard';
import { Link } from 'react-router-dom';

const Home = () => {
	// window.location.reload();

	const [categories, setCategories] = useState([]);
	const [allActivities, setAllActivities] = useState([]);

	const getAllCategories = async () => {
		const response = await axios.get(`${BASE_URL}/categories`);
		const categoryArray = { ...response.data };
		console.log(categoryArray);
		allActivities.forEach((activity) => {
			for (let i = 0; i < categoryArray.length; i++) {
				if (activity.categoryId === categoryArray[i].id) {
					categoryArray[i].activityNumber = categoryArray[i]
						.activityNumber
						? (categoryArray.activityNumber += 1)
						: 1;
				}
			}
		});
		setCategories(response.data);
	};

	const getAllActivities = async () => {
		const response = await axios.get(`${BASE_URL}/activities`);
		setAllActivities(response.data);
	};

	// const getTotalActivityNumber = () => {
	// 	let total = 0;
	// 	categories.forEach((category) => {
	// 		if (activity.categoryId === id) {
	// 			total += 1;
	// 		}
	// 	});
	// 	setActivityNumber(total);
	// };

	useEffect(() => {
		getAllCategories();
		getAllActivities();
	}, []);

	return (
		<div>
			<h1>Activities & Events Around You!</h1>
			{categories.map((category) => (
				<Link key={category.id} to={`/category/${category.id}`}>
					<CategoryCard
						id={category.id}
						name={category.name}
						image={category.image}
						allActivities={allActivities}
					/>
				</Link>
			))}
		</div>
	);
};

export default Home;
