import { Link } from 'react-router-dom'
import Search from './Search'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
import {BASE_URL} from '../globals'

const Nav = ({ authenticated, user, handleLogOut }) => {
  const [searched, toggleSearched] = useState(false)
  const [searchResults, setSearchResults] = useState('')
  const [searchQuery, setSearchQuery] = useState('')

  const navigate = useNavigate()

  const getSearchResults = async (e) => {
    e.preventDefault()
    const response = await axios.get(`${BASE_URL}/activities`)
    toggleSearched(true)
    setSearchResults(response.data)
    setSearchQuery('')
    navigate('/search-results')
  }

  const handleChange = (e) => {
    setSearchQuery(e.target.value.toLowerCase())
  }

  let authenticatedOptions
  if (user) {
    authenticatedOptions = (
      <nav>
        <Link to="/">Home</Link>
        <Link to="/about">About</Link>
        <Search onSubmit={getSearchResults} handleChange={handleChange} value={searchResults}/>
        <Link to="/profile">Profile</Link>
        <Link to="/add-activity">Add Activity</Link>
        <Link onClick={handleLogOut} to="/">
          Sign Out
        </Link>
      </nav>
    )
  }

  const publicOptions = (
    <nav>
      <Link to="/">Home</Link>
      <Link to="/about">About</Link>
      <Search onSubmit={getSearchResults} handleChange={handleChange} value={searchQuery}/>
      <Link to="/sign-in">Login</Link>
      <Link to="/register">Register</Link>
    </nav>
  )

  return (
    <div>{authenticated && user ? authenticatedOptions : publicOptions}</div>
  )
}

export default Nav
