import axios from "axios"
import { BASE_URL } from "../globals"

const LikeActivityButton = ({likedActivity, toggleLikedActivity, userId, selectActivityId, setUserActivityList}) => {
    // const handleLikeClick = async () => {
    //     await axios.post(`${BASE_URL}/user-activities/user/${userId}/activity/${selectActivityId}`)
    //     toggleLikedActivity(!likedActivity)
    // }

    const handleDropClick = async () => {
        const response = await axios.get(`${BASE_URL}/user-activities/user/${userId}`)
        const userActivityListBeforeDelete = response.data
        const userActivity = userActivityListBeforeDelete.filter(userActivity => userActivity.activityListedId === selectActivityId)
        if (userActivity[0]) {
            await axios.delete(`${BASE_URL}/user-activities/${userActivity.id}`)
        }
        const responseAfterDelete = await axios.get(`${BASE_URL}/user-activities/user/${userId}`)
        setUserActivityList(responseAfterDelete.data)
        toggleLikedActivity(!likedActivity)
    }

    return (
        <div>
            {/* {!likedActivity && <button onClick={handleLikeClick}>Like Activity</button>} */}
            {likedActivity && <button onClick={handleDropClick}>Drop Activity</button>}
        </div>
    )
}

export default LikeActivityButton
